import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  useEffect(() => {
    document.title = 'Fashion Blog - Login';
  }, []);

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    const loginData = {
      username: username,
      password: password,
    };

    try {
      const response = await fetch('http://localhost:81/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (response.ok) {
        const data = await response.json();
        setIsLoggedIn(true);
        localStorage.setItem('token', data.token);
        navigate('/'); // Redirect to Home after successful login
      } else {
        alert('Login failed. Please check your username and password.');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <body>
    <div className="body_login">
        <div class="wapper">
          <form acction="">
            <h1>login</h1>
            <div className="input-box">
                <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="i1" viewBox="0 0 16 16">
  <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6"/>
</svg> 
            </div>
            <div className="input-box">
                
                <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-lock-fill" viewBox="0 0 16 16">
  <path d="M8 1a2 2 0 0 1 2 2v4H6V3a2 2 0 0 1 2-2m3 6V3a3 3 0 0 0-6 0v4a2 2 0 0 0-2 2v5a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V9a2 2 0 0 0-2-2"/>
</svg>
            </div>
            <div className="remember-forgot">
              <label><input type="checkbox"></input>Remember me</label>
              <a><Link to="/">Forgot password</Link></a>
            </div>
            <button type="submit" className="bnt" onClick={handleLogin}>Login</button>
             <div className="register">
              <p>Don't have account?<Link to="/Register"> Create one</Link></p>
             </div>

          </form>
        </div>
    </div>
    </body>
  );
}

export default Login;
