import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './home.css';
import {images} from '../../images/images'; // Import hình ảnh từ file images.js

function Home() {
  const [posts, setPosts] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0); // Chỉ mục hình ảnh hiện tại

  useEffect(() => {
    document.title = 'Fashion Blog - Home';
    getPosts();
  }, []);

  const getPosts = () => {
    fetch('http://localhost:81/getPosts', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setPosts(data);
        // Không cần xử lý hình ảnh ở đây nữa vì đã được import
      })
      .catch((error) => console.error('Error:', error));
  };

  const Prev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const Next = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div>
      <div className='nav-container'>
        <div className='home-menu'>
          <div className='nav-menu'>
            <ul>
              <li><Link to="/">Home</Link></li>
              <li><Link to="Products">Products</Link></li>
              <li><Link to="Albums">Albums</Link></li>
              <li><Link to="About">About Us</Link></li>
              <li><Link to="Login">Login</Link></li>
            </ul>
          </div>
          <div className='welcome'>
            <h1>Fashion Blog</h1>
          </div>
        </div>
      </div>
      <div className='container'>
        <div className='side-nav'>
          {posts.map((post) => (
            <div key={post.id} className='side-nav-item'>
              <h3>{post.title}</h3>
              <p>{post.content}</p>
            </div>
          ))}
        </div>
        <div className='main-page'>
          <div className='image-container'>
            {images.length > 0 && (
              <img src={images[currentIndex]} alt="Banner" className='banner-image' />
            )}
          </div>
          <div className='prev-btn' onClick={Prev}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M15 6L9 12L15 18" stroke="black" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </div>
          <div className='next-btn' onClick={Next}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M9 6L15 12L9 18" stroke="black" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </div>
        </div>
      </div>
      <div className='footer'>FOOTER HERE</div>
    </div>
  );
}

export default Home;
