import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './profile.css';

function Profile() {
  const [user, setUser] = useState({ name: '', account_id: '' });

  useEffect(() => {
    document.title = 'Fashion Blog - Profile';
    const storedAccountId = localStorage.getItem('account_id');
    if (storedAccountId) {
      getUser(storedAccountId);
    }
  }, []);

  const getUser = (account_id) => {
    fetch(`http://localhost:81/getUser?account_id=${account_id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setUser(data); // data should be in the form { name: "User Name" }
      })
      .catch((error) => console.error('Error:', error));
  };

  return (
    <div>
        <h1>Name: {user.name}</h1>
    </div>
  );
}

export default Profile;
