import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  useEffect(() => {
    document.title = 'Fashion Blog - Login';
  }, []);

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    const loginData = {
      username: username,
      password: password,
    };

    try {
      const response = await fetch('http://localhost:81/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (response.ok) {
        const data = await response.json();
        setIsLoggedIn(true);
        localStorage.setItem('token', data.token);
        navigate('/'); // Redirect to Home after successful login
      } else {
        alert('Login failed. Please check your username and password.');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
        <div class="wapper">
          <form acction="">
            <h1>login</h1>
            <div className="input-box">
                <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
           
                <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="remember-forgot">
              <label><input type="checkbox"></input>Remember me</label>
              <a><Link to="/">Forgot password</Link></a>
            </div>
            <button type="submit" className="bnt" onClick={handleLogin}>Login</button>
             <div className="register">
              <p>Don't have account?<Link to="/Register"> Create one</Link></p>
             </div>

          </form>
        </div>
    </div>
  );
}

export default Login;
