import React, { useState, useEffect } from 'react';
import './profile.css';

function Profile() {
  const [user, setUser] = useState(null);
  const accountId = localStorage.getItem('accountId'); // Use correct key

  useEffect(() => {
    document.title = 'Fashion Blog - Profile';
    if (accountId) {
      getUser(accountId);
    } else {
      console.error('Account ID is missing');
    }
  }, [accountId]);

  const getUser = (id) => {
    fetch(`http://localhost:81/getUser?account_id=${id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
    .then(response => {
      if (response.ok) return response.json();
      throw new Error('Network response was not ok.');
    })
    .then(data => {
      if (data) {
        console.log("User's name is:", data.name);
        setUser(data);
      } else {
        console.error('User not found');
      }
    })
    .catch(error => console.error('Error:', error));
  }

  return (
    <div>
      <h1>Name: {user ? user.name : 'Loading...'}</h1>
    </div>
  );
}

export default Profile;
